  // }

  return (
    <Container>


      <Titulo>{nome}</Titulo>  

      <App />     


      <Botao
        onPress={() => props.navigation.navigate('EditarNobreak')}
      >
        <TextoBtn>Editar nobreak</TextoBtn>
      </Botao>

    </Container>
  );
}
